import React, { useState } from 'react';

const AddItemForm: React.FC = () => {
  const [id, setId] = useState('');
  const [first_name, setFirstName] = useState('');
  const [email, setEmail] = useState('');

  const handleSubmit = async (event: React.FormEvent<HTMLFormElement>) => {
    event.preventDefault();

    const newItem = { id, first_name };
    const response = await fetch('https://reqres.in/api/users', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json'
      },
      body: JSON.stringify(newItem)
    });

    if (response.ok) {
      // item was successfully added to the API
      // you can display a success message or redirect the user
    } else {
      // there was an error adding the item to the API
      // you can display an error message to the user
    }
  };

  return (
    <form onSubmit={handleSubmit}>
      <label>
        ID:
        <input type="id" value={id} onChange={(event) => setId(event.target.value)} />
      </label>
      <br />
      <label>
        FirstName:
        <input type="first_name" value={first_name} onChange={(event) => setFirstName(event.target.value)} />
      </label>
      <br />
      <label>
        Email:
        <input type="email" value={email} onChange={(event) => setEmail(event.target.value)} />
      </label>
      <br />


      <button type="submit">Add Item</button>
    </form>
  );
};

export default AddItemForm;
